import DetailNavBar from "./childComps/DetailNavBar.vue";
import { getDetail, Goods, Shop, GoodsParam, getRecommend } from "../../../network/detail";
import DetailSwiper from "./childComps/DetailSwiper.vue";
import DetailBaseInfo from "./childComps/DetailBaseInfo.vue";
import DetailShopInfo from "./childComps/DetailShopInfo.vue";
import Scroll from "../../components/common/Scroll/Scroll.vue";
import DetailGoodsInfo from "./childComps/DetailGoodsInfo.vue";
import DetailParamInfo from "./childComps/DetailParamInfo.vue";
import DetailCommentInfo from "./childComps/DetailCommentInfo.vue";
import GoodsList from "../../components/content/goods/GoodsList.vue";
import { itemListenerMixin } from "../../common/mixin";
export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    Scroll: Scroll,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailCommentInfo: DetailCommentInfo,
    GoodsList: GoodsList
  },
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {},
      commentInfo: {},
      recommendList: [],
      itemImgListener: null,
      themeTopYs: []
    };
  },
  created: function created() {
    var _this = this;

    // console.log(this.$route.params);
    this.iid = this.$route.params.iid; // 2.根据iid请求详情数据

    getDetail(this.iid).then(function (res) {
      // 1.获取顶部的图片轮播数据
      // console.log(res);
      var data = res.data.result;
      _this.topImages = data.itemInfo.topImages; // 2.获取商品信息

      _this.goods = new Goods(data.itemInfo, data.columns, data.shopInfo.services); // 3.创建店铺信息的对象

      _this.shop = new Shop(data.shopInfo); // 4.保存商品的详情数据

      _this.detailInfo = data.detailInfo; // 5.获取参数的信息

      _this.paramInfo = new GoodsParam(data.itemParams.info, data.itemParams.rule); //获取评论信息

      if (data.rate.cRate !== 0) {
        _this.commentInfo = data.rate.list[0];
      }

      getRecommend().then(function (res, error) {
        // console.log(res);
        if (error) return;
        _this.recommendList = res.data.data.list;
      }); // this.$nextTick(() => {
      // });
    });
  },
  updated: function updated() {
    this.themeTopYs = [];
    this.themeTopYs.push(0);
    this.themeTopYs.push(this.$refs.params.$el.offsetTop);
    this.themeTopYs.push(this.$refs.comment.$el.offsetTop);
    this.themeTopYs.push(this.$refs.recommend.$el.offsetTop);
    console.log(this.themeTopYs);
  },
  methods: {
    imageLoad: function imageLoad() {
      this.$ref.scroll.refresh();
    },
    titleClick: function titleClick(index) {
      console.log(index);
      this.$ref.scroll.scrollTo(0, -this.themeTopYs[index], 100);
    }
  },
  mixins: [itemListenerMixin],
  mounted: function mounted() {},
  destroyed: function destroyed() {
    this.$bus.$off("itemImageLoad", this.itemImgListener);
  }
};
