import NavBar from "../../components/common/navbar/NavBar.vue";
import HomeSwiper from './childCom/index.vue';
import { getHomeMultidata } from "../../../network/home";
import RecommendView from "./childCom/RecommendView.vue";
import FeatureView from './childCom/FeatureView.vue';
export default {
  components: {
    NavBar: NavBar,
    HomeSwiper: HomeSwiper,
    RecommendView: RecommendView,
    FeatureView: FeatureView
  },
  data: function data() {
    //用来保存拿到后的数据
    return {
      banners: [],
      recommends: []
    };
  },
  created: function created() {
    //请求多个数据
    this.getSData(); // .catch(error=>{
    //   console.log(error);
    // })
  },
  methods: {
    getSData: function getSData() {
      var _this = this;

      getHomeMultidata().then(function (res) {
        //console.log(res);
        _this.banners = res.data.data.banner.list;
        _this.recommends = res.data.data.recommend.list;
      });
    }
  }
};
